function navDynamic(){function hiddenSection(){$("section").hide()}function nav(){const url=window.location.hash;switch(url){case"#home":hiddenSection();$("#home").show();break;case"#projects":hiddenSection();$("#projects").show();break;case"#resume":hiddenSection();$("#resume").show();break;default:hiddenSection();$("#home").show()}}window.addEventListener("hashchange",nav);window.addEventListener("DOMContentLoaded",nav)}navDynamic();